#!/bin/bash

# Dizinleri oluştur
mkdir -p android/app/src/main/res/mipmap-mdpi
mkdir -p android/app/src/main/res/mipmap-hdpi
mkdir -p android/app/src/main/res/mipmap-xhdpi
mkdir -p android/app/src/main/res/mipmap-xxhdpi
mkdir -p android/app/src/main/res/mipmap-xxxhdpi

# Base64 PNG iconları oluştur (48x48 basit mavi icon)
# mdpi - 48x48
echo "" | base64 -d > android/app/src/main/res/mipmap-mdpi/ic_launcher.png

# hdpi - 72x72
echo "" | base64 -d > android/app/src/main/res/mipmap-hdpi/ic_launcher.png

# xhdpi - 96x96
echo "" | base64 -d > android/app/src/main/res/mipmap-xhdpi/ic_launcher.png

# xxhdpi - 144x144
echo "" | base64 -d > android/app/src/main/res/mipmap-xxhdpi/ic_launcher.png

# xxxhdpi - 192x192
echo "" | base64 -d > android/app/src/main/res/mipmap-xxxhdpi/ic_launcher.png

echo "Icon dosyaları oluşturuldu!"
